import React, { Component } from "react";
import css from "./counter.module.css";
import IncrementtButton from "./IncrementtButton";

export default class counter extends Component {
  constructor() {
    super();

    this.state = {
      currentCounter: 2,
      steps: 0,
    };
  }

  handleButtonClick = () => {
    const { currentCounter, steps } = this.state;

    this.setState({
      currentCounter: currentCounter - 1,
      steps: steps + 1,
    });
  };

  handleButtonClick = (clickType) => {
    const { currentCounter, steps } = this.state;

    this.setState({
      currentCounter:
        clickType === "+" ? currentCounter + 1 : currentCounter - 1,
      steps: steps + 1,
    });
  };

  render() {
    const { currentCounter, steps } = this.state;

    return (
      <div className={css.counterContainer}>
        <button
          onClick={this.handleButtonDownClick}
          className="waves-effect waves-light btn red darken-4"
        >
          -
        </button>

        <span className={css.counterValue}>{currentCounter}</span>

        <IncrementtButton onIncrement={this.handleButtonClick} />

        <span className={css.counterValue}>({steps})</span>
      </div>
    );
  }
}
