import React, { Component } from "react";

export default class Toggle extends Component {
  render() {
    return (
      <div className="switch">
        <label>
          Mostrar usuários:
          <input type="checkbox" onChange={this.handleShowUsers} />
          <span className="lever"></span>
        </label>
      </div>
    );
  }
}
